import React from 'react';
import './TodoList.css'
const TodoList = (props) => {
    let style = {
        color: "red",
        listStyleType: 'none'
    }          
    return (
        <ul style={style}>
            {props.todos.map((item) => (

                <li key={item.id} className={item.status ? "completed" : ""}>
                    <input 
                        onChange={() => props.changeStatus(item.id)}
                        type="checkbox" 
                    />
                    {item.task}
                    <button>&times;</button>
                </li>
            ))}
        </ul>
    );
};

export default TodoList;
