console.log();
  }

  return (
    <div className="app">
      <AddTodo handleTask={handleTask} />
      <TodoList
        todos={todos}
        changeStatus={changeStatus}
        handleDelete={handleDelete}
        handleEdit={handleEdit}
      />
      {modal ? <EditModal editTodo={editTodo} handleSaveTask={handleSaveTask} /> : null}
    </div>
  );
}

export default App;
